    const mapC = map;
    //mapC.removeLayer(povRef.current)
    var country = document.getElementById('country').value
    var lat = latCent(country)
    var lon = lonCent(country)
    var zoom = zoomLevelVar(country)
    mapC.setView([lat,lon],zoom)
    console.log(country)
    var url = urlToQuery()
    mapC.remove()
    LeafletMap()
  }  
  
  const toggle=(clickState)=>{
      //console.log(clickState)
      setClickState(!clickState)};
      //console.log(clickState)

