const LeafletMap = (clickState, clickState3) => {
  //console.log("in LM" + JSON.stringify(clickState.tog))
  const [center, setCenter] = useState({ lat: 38.95672363296182, lng: -77.04749867734917 });
  const zoomLevel = 14;
  return (
    <MapContainer center={center} zoom={zoomLevel} maxZoom={35}> 
    <LocationFinderDummy tog={clickState.tog} />
      {/*The LayersControl tag help us organize our layers into baselayers and tilelayers*/}
      <TileLayer
            attribution='&copy'
            url="http://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}.png"
            opacity={0.5} />
       <WaterData />
       <Trails />
       <Parking />
       <Buildings />
       <Roads />
       <Buffer />
       <BoundaryData />
      <LayersControl position="topright">
        <LayersControl.Overlay name="Points of Interest" checked>
          <FeatureGroup><POIs /></FeatureGroup>
        </LayersControl.Overlay>
        <LayersControl.Overlay name="User Provided Experiences" checked>
          <FeatureGroup><Feels/></FeatureGroup>
        </LayersControl.Overlay>
        <LayersControl.Overlay name="10 Foot Contours" unchecked>
          <Contours />
        </LayersControl.Overlay>
      </LayersControl>
      <ButtonEvents but={clickState3.but}/>
    </MapContainer>
  );
  
};
